    var lcid=1;
    function check() {
        if ($("input[name=price]").val() == '') {
            $(document).dialog({infoText: '存入金额不符合系统要求'});
            return false;
        }
        return true;
    }
    $(function () {
        $('.Cash_num_password>span').eq(0).trigger("click");;


    });

    $('.Cash_num_password>span').click(function () {
        var cid = $(this).data('id');
        $('.Cash_num_password').attr('data-id',cid);
        $('.Cash_num_password>span').removeClass('active');
        $(this).addClass('active')
    });

    function yjsy() {
        var price = $("#price").val();
        if(price<=0) return false;
        $.ajax({
            url: '/index/ctrl/deposityj',
            data: {price: price, lcid: lcid},
            type: 'POST',
            success: function (data) {
                $("#yjsy").val(data.data);
            }
        });
    }


    $("#price").keyup(function () {
        yjsy();
    })

    $(".save-btn").on('click', function () {
        if (check()) {
            var loading = null;
            var price = $("input[name=price]").val();
            $.ajax({
                url: "<?php echo url('lixibao_ru'); ?>",
                data: {price:price,lcid:lcid},
                type: 'POST',
                beforeSend: function () {
                    loading = $(document).dialog({
                        type: 'notice',
                        infoIcon: '/static_new/img/loading.gif',
                        infoText: '正在加载中',
                        autoClose: 0
                    });
                },
                success: function (data) {
                    if (data.code == 0) {
                        $(document).dialog({infoText: data.info});
                        setTimeout(function () {
                            loading.close();
                            window.location.href = '/index/ctrl/lixibao';
                        }, 2000);

                    } else {
                        $(document).dialog({infoText: data.info});
                        loading.close();
                    }
                }
            });
        }
    })
</script></body></html>